import React, { useState } from 'react';
import './App.css';

// Função para gerar uma cor aleatória
const generateRandomColor = () => {
  const letters = '0123456789ABCDEF';
  let color = '#';
  for (let i = 0; i < 6; i++) {
    color += letters[Math.floor(Math.random() * 16)];
  }
  return color;
};

const App = () => {
  const [people, setPeople] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [showList, setShowList] = useState(false);
  const [formData, setFormData] = useState({ name: '', email: '', birthdate: '', id: null });
  const [errors, setErrors] = useState({});
  const [successMessage, setSuccessMessage] = useState('');

  // Função de validação
  const validateForm = () => {
    let formErrors = {};
    const { name, email, birthdate } = formData;

    if (!name) formErrors.name = 'Nome é obrigatório!';
    if (!email || !/\S+@\S+\.\S+/.test(email)) formErrors.email = 'E-mail inválido!';
    if (!birthdate) formErrors.birthdate = 'Data de nascimento é obrigatória!';

    return formErrors;
  };

  // Função para lidar com o envio do formulário
  const handleSubmit = (e) => {
    e.preventDefault();
    const validationErrors = validateForm();
    if (Object.keys(validationErrors).length === 0) {
      if (formData.id) {
        const updatedPeople = people.map((person) => person.id === formData.id ? { ...formData } : person);
        setPeople(updatedPeople);
        setSuccessMessage('Pessoa atualizada com sucesso!');
      } else {
        setPeople([...people, { ...formData, id: Date.now(), color: generateRandomColor() }]);
        setSuccessMessage('Pessoa cadastrada com sucesso!');
      }
      setTimeout(() => setSuccessMessage(''), 3000);
      setFormData({ name: '', email: '', birthdate: '', id: null });
      setShowForm(false);
      setShowList(true);
      setErrors({});
    } else {
      setErrors(validationErrors);
    }
  };

  // Função para manipular as mudanças no formulário
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({ ...prevData, [name]: value }));
  };

  // Função para exibir a lista de pessoas
  const handleList = () => {
    setShowList(true);
    setShowForm(false);
    setErrors({});
  };

  // Função para exibir o formulário de cadastro
  const handleCreate = () => {
    setShowForm(true);
    setShowList(false);
    setFormData({ name: '', email: '', birthdate: '', id: null });
    setErrors({});
  };

  // Função para excluir uma pessoa
  const handleDelete = (id) => {
    const updatedPeople = people.filter((person) => person.id !== id);
    setPeople(updatedPeople);
  };

  return (
    <div className="App">
      <div className="header-container">
        <h1 className="animated-title">Cadastro de Pessoas</h1>
        <p className="animated-subtitle">Gerencie seus cadastros com facilidade.</p>
      </div>

      <div className="buttons-container">
        <button className="create-btn" onClick={handleCreate}>Cadastrar</button>
        <button className="list-btn" onClick={handleList}>Listar</button>
      </div>

      {successMessage && <div className="success-message">{successMessage}</div>}

      {showForm && (
        <div className="form-container">
          <h2>{formData.id ? 'Editar Pessoa' : 'Cadastrar Pessoa'}</h2>
          <form onSubmit={handleSubmit}>
            <div className="input-container">
              <label htmlFor="name">Nome</label>
              <input type="text" name="name" id="name" value={formData.name} onChange={handleChange} />
              {errors.name && <div className="error-balloon">{errors.name}</div>}
            </div>
            <div className="input-container">
              <label htmlFor="email">E-mail</label>
              <input type="email" name="email" id="email" value={formData.email} onChange={handleChange} />
              {errors.email && <div className="error-balloon">{errors.email}</div>}
            </div>
            <div className="input-container">
              <label htmlFor="birthdate">Data de Nascimento</label>
              <input type="date" name="birthdate" id="birthdate" value={formData.birthdate} onChange={handleChange} />
              {errors.birthdate && <div className="error-balloon">{errors.birthdate}</div>}
            </div>
            <button type="submit" className="save-btn">Salvar</button>
            <button type="button" className="cancel-btn" onClick={() => setShowForm(false)}>Cancelar</button>
          </form>
        </div>
      )}

      {showList && (
        <div className="list-container">
          <h2>Lista de Pessoas</h2>
          <div className="cards-container">
            {people.map((person) => (
              <div key={person.id} className="card" style={{ backgroundColor: person.color }}>
                <h3>{person.name}</h3>
                <p>{person.email}</p>
                <p>{person.birthdate}</p>
                <button className="cancel-btn" onClick={() => handleDelete(person.id)}>Excluir</button>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default App;
