import React, { useState, useEffect } from 'react';

const PersonForm = ({ onSave, personToEdit }) => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [date, setDate] = useState('');

  useEffect(() => {
    if (personToEdit) {
      setName(personToEdit.name);
      setEmail(personToEdit.email);
      setDate(personToEdit.date);
    }
  }, [personToEdit]);

  const handleSubmit = (e) => {
    e.preventDefault();
    const person = { id: personToEdit ? personToEdit.id : null, name, email, date };
    onSave(person);
    setName('');
    setEmail('');
    setDate('');
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        value={name}
        onChange={(e) => setName(e.target.value)}
        placeholder="Nome"
        required
      />
      <input
        type="email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        placeholder="E-mail"
        required
      />
      <input
        type="date"
        value={date}
        onChange={(e) => setDate(e.target.value)}
        placeholder="Data"
        required
      />
      <button type="submit">{personToEdit ? 'Atualizar' : 'Criar'}</button>
    </form>
  );
};

export default PersonForm;
