import React, { useState } from "react";

function PersonCard({ personToEdit, onSave }) {
  const [person, setPerson] = useState({
    name: personToEdit ? personToEdit.name : "",
    email: personToEdit ? personToEdit.email : "",
    date: personToEdit ? personToEdit.date : "",
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setPerson({ ...person, [name]: value });
  };

  const handleSave = () => {
    onSave(person); // Salva a pessoa
    setPerson({ name: "", email: "", date: "" }); // Limpa o formul치rio ap칩s salvar
  };

  return (
    <div className="card">
      <div className="card-body">
        <input
          type="text"
          name="name"
          placeholder="Nome"
          value={person.name}
          onChange={handleInputChange}
        />
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={person.email}
          onChange={handleInputChange}
        />
        <input
          type="date"
          name="date"
          value={person.date}
          onChange={handleInputChange}
        />
      </div>
      <div className="card-footer">
        <button onClick={handleSave} className="save-btn">
          Salvar
        </button>
      </div>
    </div>
  );
}

export default PersonCard;
