import React, { useState } from 'react';
import './App.css';
import { formatDateToBR } from './service/Utils'; // Importar a função de formatação de data

const App = () => {
  const [people, setPeople] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [showList, setShowList] = useState(false);
  const [formData, setFormData] = useState({ name: '', email: '', birthdate: '' });
  const [errors, setErrors] = useState({});
  const [successMessage, setSuccessMessage] = useState('');

  // Função de validação
  const validateForm = () => {
    let formErrors = {};
    const { name, email, birthdate } = formData;

    if (!name) formErrors.name = 'Nome é obrigatório!';
    if (!email || !/\S+@\S+\.\S+/.test(email)) formErrors.email = 'E-mail inválido!';
    if (!birthdate) formErrors.birthdate = 'Data de nascimento é obrigatória!';

    return formErrors;
  };

  // Função para lidar com o envio do formulário
  const handleSubmit = (e) => {
    e.preventDefault();

    const validationErrors = validateForm();

    if (Object.keys(validationErrors).length === 0) {
      if (formData.id) {
        // Atualiza a pessoa existente
        const updatedPeople = people.map((person) =>
          person.id === formData.id ? { ...formData } : person
        );
        setPeople(updatedPeople);
        setSuccessMessage('Pessoa editada com sucesso!');

        // Exibe a mensagem de sucesso e volta para a lista após 3 segundos
        setTimeout(() => {
          setShowForm(false);
          setShowList(true);
          setSuccessMessage('');
        }, 3000);
      } else {
        // Cria uma nova pessoa
        setPeople([...people, { ...formData, id: Date.now() }]);
        setSuccessMessage('Pessoa cadastrada com sucesso!');

        // Não volta para a lista, apenas mantém o formulário aberto
        setTimeout(() => setSuccessMessage(''), 3000);
      }

      setFormData({ name: '', email: '', birthdate: '' });
      setErrors({});
    } else {
      setErrors(validationErrors);
    }
  };

  // Função para manipular as mudanças no formulário
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({ ...prevData, [name]: value }));
  };

  // Função para exibir a lista de pessoas
  const handleList = () => {
    setShowList(true);
    setShowForm(false);
  };

  // Função para exibir o formulário de cadastro
  const handleCreate = () => {
    setShowForm(true);
    setShowList(false);
  };

  // Função para editar a pessoa
  const handleEdit = (id) => {
    const personToEdit = people.find((person) => person.id === id);
    setFormData({ ...personToEdit });
    setShowForm(true);
    setShowList(false);
  };

  // Função para excluir a pessoa
  const handleDelete = (id) => {
    const confirmDelete = window.confirm('Tem certeza que deseja excluir?');
    if (confirmDelete) {
      setPeople(people.filter((person) => person.id !== id));
    }
  };

  return (
    <div className="App">
      <div className="header-container">
        <h1>Desafio Cadastro de Pessoas</h1>
      </div>

      <div className="buttons-container">
        <button className="create-btn" onClick={handleCreate}>Cadastrar Pessoa</button>
        <button className="list-btn" onClick={handleList}>Listar Pessoas</button>
      </div>

      {showForm && (
        <div className="form-container">
          <h2>{formData.id ? 'Editar Pessoa' : 'Cadastrar Pessoa'}</h2>
          {successMessage && (
            <div className="success-message">{successMessage}</div>
          )}
          <form onSubmit={handleSubmit}>
            <div className="input-container">
              <label>Nome:</label>
              <input
                type="text"
                name="name"
                value={formData.name}
                onChange={handleChange}
              />
              {errors.name && <div className="error-balloon">{errors.name}</div>}
            </div>

            <div className="input-container">
              <label>E-mail:</label>
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
              />
              {errors.email && <div className="error-balloon">{errors.email}</div>}
            </div>

            <div className="input-container">
              <label>Data de Nascimento:</label>
              <input
                type="date"
                name="birthdate"
                value={formData.birthdate}
                onChange={handleChange}
              />
              {errors.birthdate && <div className="error-balloon">{errors.birthdate}</div>}
            </div>

            <button className="submit-btn" type="submit">{formData.id ? 'Salvar' : 'Cadastrar'}</button>
          </form>
        </div>
      )}

      {showList && (
        <div className="list-container">
          <h2>Lista de Pessoas</h2>
          {people.length === 0 ? (
            <p>Não há pessoas cadastradas.</p>
          ) : (
            people.map((person) => (
              <div key={person.id} className="card">
                <p><strong>{person.name}</strong></p>
                <p>{person.email}</p>
                <p>{formatDateToBR(person.birthdate)}</p>
                <button className="save-btn" onClick={() => handleEdit(person.id)}>Editar</button>
                <button className="cancel-btn" onClick={() => handleDelete(person.id)}>Excluir</button>
              </div>
            ))
          )}
        </div>
      )}
    </div>
  );
};

export default App;
