import React, { useState, useEffect } from 'react';
import PersonForm from './components/PersonForm';
import PersonList from './components/PersonList';
import * as PersonService from './services/PersonService';

const App = () => {
  const [people, setPeople] = useState([]);
  const [editingPerson, setEditingPerson] = useState(null);

  // Carrega as pessoas ao iniciar o aplicativo
  useEffect(() => {
    setPeople(PersonService.getAllPeople());
  }, []);

  // Função para salvar (criar ou editar)
  const handleSave = (person) => {
    if (person.id) {
      PersonService.updatePerson(person);
    } else {
      PersonService.addPerson(person);
    }
    setPeople(PersonService.getAllPeople());
    setEditingPerson(null);
  };

  // Função para editar uma pessoa
  const handleEdit = (person) => {
    setEditingPerson(person);
  };

  // Função para deletar uma pessoa
  const handleDelete = (id) => {
    PersonService.deletePerson(id);
    setPeople(PersonService.getAllPeople());
    setEditingPerson(null);
  };

  // Função para limpar o formulário
  const handleClear = () => {
    setEditingPerson(null);
  };

  return (
    <div className="app-container">
      <h1>CRUD de Pessoas</h1>
      <PersonForm person={editingPerson} onSave={handleSave} onClear={handleClear} />
      <PersonList people={people} onEdit={handleEdit} onDelete={handleDelete} />
    </div>
  );
};

export default App;
