import React, { useState } from 'react';
import './App.css';
import Modal from './components/Modal';

// Função para gerar uma cor aleatória em formato hex
const generateRandomColor = () => {
  const letters = '0123456789ABCDEF';
  let color = '#';
  for (let i = 0; i < 6; i++) {
    color += letters[Math.floor(Math.random() * 16)];
  }
  return color;
};

// Função para converter de hex para RGBA e adicionar opacidade
const hexToRgba = (hex, opacity = 0.7) => {
  hex = hex.replace('#', '');

  // Extrai os valores de R, G e B
  const r = parseInt(hex.substring(0, 2), 16);
  const g = parseInt(hex.substring(2, 4), 16);
  const b = parseInt(hex.substring(4, 6), 16);

  // Retorna a cor no formato rgba
  return `rgba(${r}, ${g}, ${b}, ${opacity})`;
};
const App = () => {
  const [people, setPeople] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [showList, setShowList] = useState(false);
  const [formData, setFormData] = useState({ name: '', email: '', birthdate: '', id: null });
  const [errors, setErrors] = useState({});
  const [successMessage, setSuccessMessage] = useState('');const [isModalOpen, setIsModalOpen] = useState(false);  // Controla o estado do modal
  const [personToDelete, setPersonToDelete] = useState(null); // Pessoa a ser deletada


  // Função de validação
  const validateForm = () => {
    let formErrors = {};
    const { name, email, birthdate } = formData;
    const today = new Date();
    const inputDate = new Date(birthdate);

    if (!name) formErrors.name = 'Nome é obrigatório!';
    if (!email || !/\S+@\S+\.\S+/.test(email)) formErrors.email = 'E-mail inválido!';
    if (!birthdate) formErrors.birthdate = 'Data de nascimento é obrigatória!';

    // Validação da data de nascimento
    if (!birthdate) {
      formErrors.birthdate = 'Data de nascimento é obrigatória!';
    } else if (inputDate > today) {
      formErrors.birthdate = 'Data de nascimento não pode ser no futuro!';
    } else if (inputDate.getFullYear() < 1900 || inputDate.getFullYear() > today.getFullYear()) {
      formErrors.birthdate = 'Ano inválido! Insira uma data entre 1900 e o ano atual.';
    }


    return formErrors;
  };

  // Função para lidar com o envio do formulário
  const handleSubmit = (e) => {
    e.preventDefault();

    const validationErrors = validateForm();

    if (Object.keys(validationErrors).length === 0) {      
      const randomColor = generateRandomColor();
      const colorWithOpacity = hexToRgba(randomColor, 0.7);
      if (formData.id) {
        // Caso tenha ID, estamos editando a pessoa
        const updatedPeople = people.map((person) =>
          person.id === formData.id ? { ...formData } : person
        );
        setPeople(updatedPeople);
        setShowList(true); // Vai para a lista
        setShowForm(false);
      } else {
        // Caso não tenha ID, estamos cadastrando uma nova pessoa
        setPeople([...people, { ...formData, id: Date.now(), color: colorWithOpacity }]);
        setSuccessMessage('Pessoa cadastrada com sucesso!');
        setTimeout(() => setSuccessMessage(''), 3000); // Limpa a mensagem após 3 segundos
        setFormData({ name: '', email: '', birthdate: '', id: null });
      }
      setErrors({}); // Limpa os erros após o envio bem-sucedido
    } else {
      setErrors(validationErrors); // Exibe os erros
    }
  };

  // Função para manipular as mudanças no formulário
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({ ...prevData, [name]: value }));
  };

  // Função para exibir a lista de pessoas
  const handleList = () => {
    setShowList(true);
    setShowForm(false);
    setErrors({}); // Limpar os erros ao navegar para a lista
  };

  // Função para exibir o formulário de cadastro
  const handleCreate = () => {
    setShowForm(true);
    setShowList(false);
    setFormData({ name: '', email: '', birthdate: '', id: null });
    setErrors({}); // Limpar os erros ao criar um novo cadastro
  };

  // Função para editar a pessoa
  const handleEdit = (id) => {
    const personToEdit = people.find((person) => person.id === id);
    setFormData({ ...personToEdit });
    setShowForm(true);
    setShowList(false);
    setErrors({}); // Limpar os erros ao editar uma pessoa
  };

  // Função para abrir o modal de exclusão
  const handleDelete = (id) => {
    setPersonToDelete(id);
    setIsModalOpen(true);  // Abre o modal de confirmação
  };

  // Função para confirmar a exclusão
  const confirmDelete = () => {
    if (personToDelete) {
      setPeople(people.filter((person) => person.id !== personToDelete));
      setIsModalOpen(false);
      setPersonToDelete(null);
    }
  };

  // Função para cancelar a exclusão
  const cancelDelete = () => {
    setIsModalOpen(false);
    setPersonToDelete(null);
  };

  return (
    <div className="App">
      <div className="header-container">
        <h1 className="animated-title">Desafio Front-end</h1>
        <p className="animated-subtitle">Cadastro e listagem de pessoas</p>
      </div>

      <div className="buttons-container">
        <button onClick={handleCreate} className="create-btn">
          Novo Cadastro
        </button>
        <button onClick={handleList} className="list-btn">
          Ver Lista
        </button>
      </div>

      {successMessage && <div className="success-message">{successMessage}</div>}

      {showForm && (
        <div className="form-container">
          <h2>{formData.id ? 'Editar Pessoa' : 'Cadastrar Pessoa'}</h2>
          <form onSubmit={handleSubmit}>
            <div className="input-container">
              <label>Nome:</label>
              <input
                type="text"
                name="name"
                value={formData.name}
                onChange={handleChange}
              />
              {errors.name && <div className="error-balloon">{errors.name}</div>}
            </div>
            <div className="input-container">
              <label>E-mail:</label>
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
              />
              {errors.email && <div className="error-balloon">{errors.email}</div>}
            </div>
            <div className="input-container">
              <label>Data de Nascimento:</label>
              <input
                type="date"
                name="birthdate"
                value={formData.birthdate}
                onChange={handleChange}
              />
              {errors.birthdate && <div className="error-balloon">{errors.birthdate}</div>}
            </div>
            <button type="submit" className="create-btn">
              {formData.id ? 'Atualizar' : 'Cadastrar'}
            </button>
          </form>
        </div>
      )}

      {showList && (
        <div className="list-container">
          <h2>Lista de Pessoas</h2>
          {people.length === 0 ? (
            <p>Não há pessoas cadastradas.</p>
          ) : (
            <div className="cards-wrapper">
              {people.map((person) => (
                <div
                  key={person.id}
                  className="card"
                  style={{ backgroundColor: person.color }}
                >
                  <p><strong>{person.name}</strong></p>
                  <p>{person.email}</p>
                  <p>{new Date(person.birthdate + 'T00:00:00').toLocaleDateString('pt-BR')}</p>
                    <div className="card-btn-space">
                      <button className="save-btn" onClick={() => handleEdit(person.id)}>Editar</button>
                      <button className="cancel-btn" onClick={() => handleDelete(person.id)}>Excluir</button>
                    </div>
                </div>
              ))}
            </div>
          )}
        </div>
      )}

      {/* Modal de confirmação de exclusão */}
      <Modal
        isOpen={isModalOpen}
        onClose={cancelDelete}
        onConfirm={confirmDelete}
      />

    </div>
  );
};

export default App;
