import React, { useState, useEffect } from "react";

function PersonCard({ personToEdit, onSave, setPersonToEdit }) {
  const [person, setPerson] = useState({
    id: "",
    name: "",
    email: "",
    date: "",
  });

  useEffect(() => {
    if (personToEdit) {
      setPerson(personToEdit); // Preenche o card com os dados da pessoa a ser editada
    } else {
      setPerson({ name: "", email: "", date: "" }); // Limpa os campos para criação
    }
  }, [personToEdit]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setPerson({ ...person, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSave(person); // Salva ou atualiza a pessoa
  };

  return (
    <div className="person-card">
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="name"
          placeholder="Nome"
          value={person.name}
          onChange={handleInputChange}
          required
        />
        <input
          type="email"
          name="email"
          placeholder="E-mail"
          value={person.email}
          onChange={handleInputChange}
          required
        />
        <input
          type="date"
          name="date"
          placeholder="Data de Nascimento"
          value={person.date}
          onChange={handleInputChange}
          required
        />
        <button type="submit">{personToEdit ? "Salvar Edição" : "Criar Pessoa"}</button>
        {personToEdit && (
          <button type="button" className="cancel-btn" onClick={() => setPersonToEdit(null)}>
            Cancelar
          </button>
        )}
      </form>
    </div>
  );
}

export default PersonCard;
