import React, { useState } from 'react';
import PersonForm from './components/PersonForm';
import PersonList from './components/PersonList';
import { addPerson, deletePerson, updatePerson } from './services/PersonService';

const App = () => {
  const [people, setPeople] = useState([]);

  const handleAddPerson = (person) => {
    const newPerson = addPerson(person); // Chama o serviço de adicionar pessoa
    setPeople((prevPeople) => [...prevPeople, newPerson]); // Atualiza o estado
  };

  const handleEditPerson = (id, updatedPerson) => {
    updatePerson(updatedPerson); // Atualiza a pessoa
    setPeople(people.map(person => person.id === id ? { ...person, ...updatedPerson } : person));
  };

  const handleDeletePerson = (id) => {
    if (window.confirm('Você tem certeza que deseja excluir esta pessoa?')) {
      deletePerson(id); // Chama o serviço de deletar
      setPeople(people.filter(person => person.id !== id)); // Atualiza o estado
    }
  };

  const handleSave = (updatedPerson) => {
    // Verifique se estamos editando ou criando uma pessoa
    if (updatedPerson.id) {
      // Editando pessoa existente
      updatePerson(updatedPerson); // Atualiza a pessoa na lista
      setPersonToEdit(null); // Limpa o estado de edição
    } else {
      // Criando uma nova pessoa
      addPerson(updatedPerson); // Adiciona uma nova pessoa
    }
  };

  return (
    <div>
      <h1>Cadastro de Pessoas</h1>
      <PersonForm onAdd={handleAddPerson} />
      <PersonList 
        people={people} 
        onEdit={handleEditPerson} 
        onDelete={handleDeletePerson} 
      />
    </div>
  );
};

export default App;
