import React, { useState, useCallback } from 'react';
import './App.css';
import Modal from './components/Modal';
import * as FaIcons from 'react-icons/fa';

const generateRandomColor = () => {
  let color = '#';
  for (let i = 0; i < 6; i++) {
    color += '0123456789ABCDEF'[Math.floor(Math.random() * 16)];
  }
  return color;
};

const hexToRgba = (hex, opacity = 0.7) => {
  const [r, g, b] = hex.match(/\w\w/g).map((x) => parseInt(x, 16));
  return `rgba(${r}, ${g}, ${b}, ${opacity})`;
};

const App = () => {
  const [people, setPeople] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({ name: '', email: '', birthdate: '', id: null });
  const [errors, setErrors] = useState({});
  const [successMessage, setSuccessMessage] = useState('');
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [personToDelete, setPersonToDelete] = useState(null);

  // Validação do formulário
  const validateForm = useCallback(() => {
    let formErrors = {};
    const { name, email, birthdate } = formData;
    const today = new Date();
    const inputDate = new Date(birthdate);

    if (!name) formErrors.name = 'Nome é obrigatório!';
    if (!email || !/\S+@\S+\.\S+/.test(email)) formErrors.email = 'E-mail inválido!';
    if (!birthdate) formErrors.birthdate = 'Data de nascimento é obrigatória!';
    if (inputDate > today) formErrors.birthdate = 'Data de nascimento não pode ser no futuro!';
    if (inputDate.getFullYear() < 1900 || inputDate.getFullYear() > today.getFullYear()) formErrors.birthdate = 'Ano inválido!';

    return formErrors;
  }, [formData]);

  // Função para adicionar ou editar pessoas
  const handleSubmit = useCallback(
    (e) => {
      e.preventDefault();
      const validationErrors = validateForm();
      if (Object.keys(validationErrors).length === 0) {
        const randomColor = generateRandomColor();
        const colorWithOpacity = hexToRgba(randomColor, 0.20);
        if (formData.id) {
          setPeople(people.map((person) => person.id === formData.id ? { ...formData } : person));
          setSuccessMessage('Pessoa atualizada com sucesso!');
        } else {
          setPeople([...people, { ...formData, id: Date.now(), color: colorWithOpacity }]);
          setSuccessMessage('Pessoa cadastrada com sucesso!');
        }
        setTimeout(() => setSuccessMessage(''), 3000);
        setFormData({ name: '', email: '', birthdate: '', id: null });
        setErrors({});
        setShowForm(false);
      } else {
        setErrors(validationErrors);
      }
    },
    [people, formData, validateForm]
  );

  // Funções para alternar entre exibição do formulário e lista
  const handleCreate = () => {
    setShowForm(true);
    setFormData({ name: '', email: '', birthdate: '', id: null });
    setErrors({});
  };

  const handleList = () => {
    setShowForm(false);
  };

  // Funções para editar e excluir
  const handleEdit = (id) => {
    const personToEdit = people.find((person) => person.id === id);
    setFormData({ ...personToEdit });
    setShowForm(true);
  };

  const handleDelete = (id) => {
    setPersonToDelete(id);
    setIsModalOpen(true);
  };

  const confirmDelete = () => {
    setPeople(people.filter((person) => person.id !== personToDelete));
    setIsModalOpen(false);
    setPersonToDelete(null);
  };

  const cancelDelete = () => {
    setIsModalOpen(false);
    setPersonToDelete(null);
  };

  return (
    <div className="App">
      <div className="header-container">
        <h1 className="animated-title">Desafio: Simbiose Ventures</h1>
        <p className="animated-subtitle">CRUD Simples: Pessoas</p>
      </div>

      <div className="buttons-container">
        <button onClick={handleCreate} className="create-btn">Novo Cadastro</button>
        <button onClick={handleList} className="list-btn">Ver Cadastros</button>
      </div>

      {successMessage && <div className="success-message">{successMessage}</div>}

      {showForm && (
        <div className="form-container">
          <h2>{formData.id ? 'Editar Pessoa' : 'Cadastrar Pessoa'}</h2>
          <form onSubmit={handleSubmit}>
            <div className="input-container">
              <label>Nome:</label>
              <input
                type="text"
                name="name"
                value={formData.name}
                onChange={(e) => setFormData({ ...formData, name: e.target.value })}
              />
              {errors.name && <div className="error-balloon">{errors.name}</div>}
            </div>
            <div className="input-container">
              <label>E-mail:</label>
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={(e) => setFormData({ ...formData, email: e.target.value })}
              />
              {errors.email && <div className="error-balloon">{errors.email}</div>}
            </div>
            <div className="input-container">
              <label>Data de Nascimento:</label>
              <input
                type="date"
                name="birthdate"
                value={formData.birthdate}
                onChange={(e) => setFormData({ ...formData, birthdate: e.target.value })}
              />
              {errors.birthdate && <div className="error-balloon">{errors.birthdate}</div>}
            </div>
            <button type="submit" className="create-btn">
              {formData.id ? 'Atualizar' : 'Cadastrar'}
            </button>
          </form>
        </div>
      )}

      {!showForm && (
        <div className="list-container">
          <h2>Pessoas Cadastradas</h2>
          {people.length === 0 ? (
            <p>Não há pessoas cadastradas.</p>
          ) : (
            <div className="cards-wrapper">
              {people.map((person) => (
                <div key={person.id} className="card" style={{ backgroundColor: person.color }}>
                  <div className="card-items-conteiner">
                    <div className="card-item">
                      <FaIcons.FaUser size={18} color="#333" className="card-icon" />
                      <strong>{person.name}</strong>
                    </div>
                    <div className="card-item">
                      <FaIcons.FaEnvelope size={18} color="#333" className="card-icon" />
                      {person.email}
                    </div>
                    <div className="card-item">
                      <FaIcons.FaBirthdayCake size={18} color="#333" className="card-icon" />
                      {new Date(person.birthdate + 'T00:00:00').toLocaleDateString('pt-BR')}
                    </div>
                  </div>
                  <div className="card-btn-space">
                    <button className="save-btn" onClick={() => handleEdit(person.id)}>Editar</button>
                    <button className="cancel-btn" onClick={() => handleDelete(person.id)}>Excluir</button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      )}

      <Modal isOpen={isModalOpen} onClose={cancelDelete} onConfirm={confirmDelete} />
    </div>
  );
};

export default App;
