import React, { useState } from 'react';
import './App.css';
import Modal from './components/Modal';  // Certifique-se de que o caminho está correto

const App = () => {
  const [people, setPeople] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [showList, setShowList] = useState(false);
  const [formData, setFormData] = useState({ name: '', email: '', birthdate: '', id: null });
  const [errors, setErrors] = useState({});
  const [successMessage, setSuccessMessage] = useState('');
  const [isModalOpen, setIsModalOpen] = useState(false);  // Controla o estado do modal
  const [personToDelete, setPersonToDelete] = useState(null); // Pessoa a ser deletada

  // Função para abrir o formulário de cadastro
  const handleCreate = () => {
    setShowForm(true);
    setShowList(false);
    setFormData({ name: '', email: '', birthdate: '', id: null });
  };

  // Função para exibir a lista de pessoas cadastradas
  const handleList = () => {
    setShowForm(false);
    setShowList(true);
  };

  // Função para adicionar ou editar uma pessoa
  const handleSubmit = (e) => {
    e.preventDefault();

    if (!validateForm()) return;

    if (formData.id) {
      setPeople(people.map((person) => person.id === formData.id ? formData : person));
      setSuccessMessage('Pessoa editada com sucesso!');
    } else {
      setPeople([...people, { ...formData, id: Date.now(), color: getRandomColor() }]);
      setSuccessMessage('Pessoa cadastrada com sucesso!');
    }

    resetForm();
  };

  // Função para validar o formulário
  const validateForm = () => {
    const newErrors = {};
    if (!formData.name) newErrors.name = 'Nome é obrigatório';
    if (!formData.email) newErrors.email = 'Email é obrigatório';
    if (!formData.birthdate) newErrors.birthdate = 'Data de nascimento é obrigatória';
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  // Função para resetar o formulário
  const resetForm = () => {
    setFormData({ name: '', email: '', birthdate: '', id: null });
    setErrors({});
    setShowForm(false);
    setShowList(true);
  };

  // Função para gerar uma cor aleatória para o card
  const getRandomColor = () => {
    const letters = '0123456789ABCDEF';
    let color = '#';
    for (let i = 0; i < 6; i++) {
      color += letters[Math.floor(Math.random() * 16)];
    }
    return color;
  };

  // Função para abrir o modal de exclusão
  const handleDelete = (id) => {
    setPersonToDelete(id);
    setIsModalOpen(true);  // Abre o modal de confirmação
  };

  // Função para confirmar a exclusão
  const confirmDelete = () => {
    if (personToDelete) {
      setPeople(people.filter((person) => person.id !== personToDelete));
      setIsModalOpen(false);
      setPersonToDelete(null);
    }
  };

  // Função para cancelar a exclusão
  const cancelDelete = () => {
    setIsModalOpen(false);
    setPersonToDelete(null);
  };

  // Função para editar os dados de uma pessoa
  const handleEdit = (id) => {
    const person = people.find((person) => person.id === id);
    setFormData({ ...person });
    setShowForm(true);
    setShowList(false);
  };

  return (
    <div className="App">
      <div className="header-container">
        <h1 className="animated-title">Desafio Front-end</h1>
        <p className="animated-subtitle">Cadastro e listagem de pessoas</p>
      </div>

      <div className="buttons-container">
        <button onClick={handleCreate} className="create-btn">
          Novo Cadastro
        </button>
        <button onClick={handleList} className="list-btn">
          Ver Lista
        </button>
      </div>

      {successMessage && <div className="success-message">{successMessage}</div>}

      {showForm && (
        <div className="form-container">
          <h2>{formData.id ? 'Editar Pessoa' : 'Cadastrar Pessoa'}</h2>
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label>Nome:</label>
              <input
                type="text"
                value={formData.name}
                onChange={(e) => setFormData({ ...formData, name: e.target.value })}
              />
              {errors.name && <span className="error">{errors.name}</span>}
            </div>
            <div className="form-group">
              <label>Email:</label>
              <input
                type="email"
                value={formData.email}
                onChange={(e) => setFormData({ ...formData, email: e.target.value })}
              />
              {errors.email && <span className="error">{errors.email}</span>}
            </div>
            <div className="form-group">
              <label>Data de Nascimento:</label>
              <input
                type="date"
                value={formData.birthdate}
                onChange={(e) => setFormData({ ...formData, birthdate: e.target.value })}
              />
              {errors.birthdate && <span className="error">{errors.birthdate}</span>}
            </div>
            <button type="submit" className="submit-btn">Salvar</button>
          </form>
        </div>
      )}

      {showList && (
        <div className="list-container">
          <h2>Lista de Pessoas</h2>
          {people.length === 0 ? (
            <p>Não há pessoas cadastradas.</p>
          ) : (
            <div className="cards-wrapper">
              {people.map((person) => (
                <div
                  key={person.id}
                  className="card"
                  style={{ backgroundColor: person.color }}
                >
                  <p><strong>{person.name}</strong></p>
                  <p>{person.email}</p>
                  <p>{new Date(person.birthdate + 'T00:00:00').toLocaleDateString('pt-BR')}</p>
                  <div className="card-btn-space">
                    <button className="save-btn" onClick={() => handleEdit(person.id)}>Editar</button>
                    <button className="cancel-btn" onClick={() => handleDelete(person.id)}>Excluir</button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      )}

      {/* Modal de confirmação de exclusão */}
      <Modal
        isOpen={isModalOpen}
        onClose={cancelDelete}
        onConfirm={confirmDelete}
      />
    </div>
  );
};

export default App;
