let people = [
    { id: 1, name: 'Jo찾o Silva', email: 'joao@email.com', birthdate: '1990-01-01' },
    { id: 2, name: 'Maria Oliveira', email: 'maria@email.com', birthdate: '1985-06-12' }
  ];
  
  export const fetchPeople = () => {
    return [...people]; // Retorna uma c처pia da lista
  };
  
  export const savePerson = (person) => {
    if (person.id) {
      people = people.map(p => (p.id === person.id ? person : p));
    } else {
      const newId = people.length ? Math.max(...people.map(p => p.id)) + 1 : 1;
      person.id = newId;
      people.push(person);
    }
    return [...people]; // Retorna a lista atualizada
  };
  
  export const deletePerson = (id) => {
    people = people.filter(person => person.id !== id);
    return [...people]; // Retorna a lista atualizada
  };
