import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [people, setPeople] = useState([]);
  const [formData, setFormData] = useState({ name: '', email: '', birthdate: '' });
  const [errors, setErrors] = useState({});
  const [successMessage, setSuccessMessage] = useState('');
  const [showList, setShowList] = useState(true);

  // Funções de manipulação
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validateForm()) {
      const newPerson = { ...formData, id: people.length + 1 };
      setPeople([...people, newPerson]);
      setFormData({ name: '', email: '', birthdate: '' });
      setErrors({});
      setSuccessMessage('Pessoa cadastrada com sucesso!');
      setTimeout(() => setSuccessMessage(''), 3000);
    }
  };

  const handleEdit = (id) => {
    const person = people.find((p) => p.id === id);
    setFormData(person);
    setShowList(false);
  };

  const handleDelete = (id) => {
    setPeople(people.filter((p) => p.id !== id));
  };

  const validateForm = () => {
    let valid = true;
    let errorMessages = {};

    if (!formData.name) {
      errorMessages.name = 'O nome é obrigatório!';
      valid = false;
    }
    if (!formData.email) {
      errorMessages.email = 'O e-mail é obrigatório!';
      valid = false;
    }
    if (!formData.birthdate) {
      errorMessages.birthdate = 'A data de nascimento é obrigatória!';
      valid = false;
    }

    setErrors(errorMessages);
    return valid;
  };

  useEffect(() => {
    if (!showList) {
      setSuccessMessage('');
    }
  }, [showList]);

  return (
    <div className="app-container">
      <header className="header-container">
        <h1>Cadastro de Pessoas</h1>
      </header>

      {showList ? (
        <div className="list-container">
          <h2>Lista de Pessoas</h2>
          {people.length === 0 ? (
            <p>Não há pessoas cadastradas.</p>
          ) : (
            people.map((person) => (
              <div key={person.id} className="card fade-in">
                <p><strong>{person.name}</strong></p>
                <p>{person.email}</p>
                <p>{new Date(person.birthdate + 'T00:00:00').toLocaleDateString('pt-BR')}</p>
                <button className="save-btn" onClick={() => handleEdit(person.id)}>Editar</button>
                <button className="cancel-btn" onClick={() => handleDelete(person.id)}>Excluir</button>
              </div>
            ))
          )}
        </div>
      ) : (
        <div className="form-container">
          <h2>{formData.id ? 'Editar Pessoa' : 'Cadastrar Pessoa'}</h2>
          {successMessage && <div className="success-message">{successMessage}</div>}
          <form onSubmit={handleSubmit}>
            <div className="input-container">
              <label>Nome:</label>
              <input
                type="text"
                name="name"
                value={formData.name}
                onChange={handleChange}
                className={errors.name ? 'error' : ''}
              />
              {errors.name && <div className="error-balloon">{errors.name}</div>}
            </div>

            <div className="input-container">
              <label>E-mail:</label>
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                className={errors.email ? 'error' : ''}
              />
              {errors.email && <div className="error-balloon">{errors.email}</div>}
            </div>

            <div className="input-container">
              <label>Data de Nascimento:</label>
              <input
                type="date"
                name="birthdate"
                value={formData.birthdate}
                onChange={handleChange}
                className={errors.birthdate ? 'error' : ''}
              />
              {errors.birthdate && <div className="error-balloon">{errors.birthdate}</div>}
            </div>

            <button className="submit-btn" type="submit">{formData.id ? 'Salvar' : 'Cadastrar'}</button>
          </form>
        </div>
      )}
    </div>
  );
}

export default App;
