import React, { useState } from 'react';
import './App.css';

// Fun√ß√£o para gerar uma cor aleat√≥ria
const generateRandomColor = () => {
  const letters = '0123456789ABCDEF';
  let color = '#';
  for (let i = 0; i < 6; i++) {
    color += letters[Math.floor(Math.random() * 16)];
  }
  return color;
};

const App = () => {
  const [people, setPeople] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [showList, setShowList] = useState(false);
  const [formData, setFormData] = useState({ name: '', email: '', birthdate: '', id: null });
  const [errors, setErrors] = useState({});
  const [successMessage, setSuccessMessage] = useState('');

  // Fun√ß√£o de valida√ß√£o
  const validateForm = () => {
    let formErrors = {};
    const { name, email, birthdate } = formData;

    if (!name) formErrors.name = 'Nome √© obrigat√≥rio!';
    if (!email || !/\S+@\S+\.\S+/.test(email)) formErrors.email = 'E-mail inv√°lido!';
    if (!birthdate) formErrors.birthdate = 'Data de nascimento √© obrigat√≥ria!';

    return formErrors;
  };

  // Fun√ß√£o para lidar com o envio do formul√°rio
  const handleSubmit = (e) => {
    e.preventDefault();

    const validationErrors = validateForm();

    if (Object.keys(validationErrors).length === 0) {
      if (formData.id) {
        // Caso tenha ID, estamos editando a pessoa
        const updatedPeople = people.map((person) =>
          person.id === formData.id ? { ...formData } : person
        );
        setPeople(updatedPeople);
        setShowList(true); // Vai para a lista
        setShowForm(false);
      } else {
        // Caso n√£o tenha ID, estamos cadastrando uma nova pessoa
        setPeople([...people, { ...formData, id: Date.now(), color: generateRandomColor() }]);
        setSuccessMessage('Pessoa cadastrada com sucesso!');
        setTimeout(() => setSuccessMessage(''), 3000); // Limpa a mensagem ap√≥s 3 segundos
        setFormData({ name: '', email: '', birthdate: '', id: null });
      }
      setErrors({}); // Limpa os erros ap√≥s o envio bem-sucedido
    } else {
      setErrors(validationErrors); // Exibe os erros
    }
  };

  // Fun√ß√£o para manipular as mudan√ßas no formul√°rio
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({ ...prevData, [name]: value }));
  };

  // Fun√ß√£o para exibir a lista de pessoas
  const handleList = () => {
    setShowList(true);
    setShowForm(false);
    setErrors({}); // Limpar os erros ao navegar para a lista
  };

  // Fun√ß√£o para exibir o formul√°rio de cadastro
  const handleCreate = () => {
    setShowForm(true);
    setShowList(false);
    setFormData({ name: '', email: '', birthdate: '', id: null });
    setErrors({}); // Limpar os erros ao criar um novo cadastro
  };

  // Fun√ß√£o para editar a pessoa
  const handleEdit = (id) => {
    const personToEdit = people.find((person) => person.id === id);
    setFormData({ ...personToEdit });
    setShowForm(true);
    setShowList(false);
    setErrors({}); // Limpar os erros ao editar uma pessoa
  };

  // Fun√ß√£o para excluir a pessoa
  const handleDelete = (id) => {
    const confirmDelete = window.confirm('Tem certeza que deseja excluir?');
    if (confirmDelete) {
      setPeople(people.filter((person) => person.id !== id));
    }
  };

  return (
    <div className="App">
      <div className="header-container">
        <h1 className="animated-title">Desafio Front-end</h1>
        <p className="animated-subtitle">Cadastro e listagem de pessoas</p>
      </div>

      <div className="buttons-container">
        <button onClick={handleCreate} className="create-btn">
          Novo Cadastro
        </button>
        <button onClick={handleList} className="list-btn">
          Ver Lista
        </button>
      </div>

      {successMessage && <div className="success-message">{successMessage}</div>}

      {showForm && (
        <div className="form-container">
          <h2>{formData.id ? 'Editar Pessoa' : 'Cadastrar Pessoa'}</h2>
          <form onSubmit={handleSubmit}>
            <div className="input-container">
              <label>Nome:</label>
              <input
                type="text"
                name="name"
                value={formData.name}
                onChange={handleChange}
              />
              {errors.name && <div className="error-balloon">{errors.name}</div>}
            </div>
            <div className="input-container">
              <label>E-mail:</label>
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
              />
              {errors.email && <div className="error-balloon">{errors.email}</div>}
            </div>
            <div className="input-container">
              <label>Data de Nascimento:</label>
              <input
                type="date"
                name="birthdate"
                value={formData.birthdate}
                onChange={handleChange}
              />
              {errors.birthdate && <div className="error-balloon">{errors.birthdate}</div>}
            </div>
            <button type="submit" className="create-btn">
              {formData.id ? 'Atualizar' : 'Cadastrar'}
            </button>
          </form>
        </div>
      )}

      {showList && (
        <div className="list-container">
          <h2>Lista de Pessoas</h2>

          <button className="toggle-btn" onClick={toggleViewMode}>
            {viewMode === 'card' ? 'Ver em Lista' : 'Ver em Cards'}
          </button>

          {people.length === 0 ? (
            <p>N√£o h√° pessoas cadastradas.</p>
          ) : (
            <div className="cards-wrapper">
              {people.map((person) => (
                <div
                  key={person.id}
                  className="card"
                  style={{ backgroundColor: person.color }}
                >
                  <p><strong>{person.name}</strong></p>
                  <p>{person.email}</p>
                  <p>{new Date(person.birthdate + 'T00:00:00').toLocaleDateString('pt-BR')}</p>
                    <div class="card-btn-space">
                      <button className="save-btn" onClick={() => handleEdit(person.id)}>‚úèÔ∏è Editar</button>
                      <button className="cancel-btn" onClick={() => handleDelete(person.id)}>üóëÔ∏è Excluir</button>
                    </div>
                </div>
              ))}
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default App;
