import React from 'react';

const PersonList = ({ people, onEdit, onDelete }) => {
  return (
    <div className="person-list">
      {people.length === 0 ? (
        <p>Nenhuma pessoa cadastrada.</p>
      ) : (
        people.map((person) => (
          <div key={person.id} className="person-card">
            <h3>{person.name}</h3>
            <p>Email: {person.email}</p>
            <p>Data de Nascimento: {new Date(person.birthDate).toLocaleDateString('pt-BR')}</p>
            <button onClick={() => onEdit(person)}>Editar</button>
            <button onClick={() => onDelete(person.id)}>Excluir</button>
          </div>
        ))
      )}
    </div>
  );
};

export default PersonList;
