import React, { useState } from 'react';
import './App.css';
import Modal from './components/Modal';
import * as FaIcons from 'react-icons/fa';

// Função para gerar uma cor aleatória em formato hex
const generateRandomColor = () => {
  const letters = '0123456789ABCDEF';
  let color = '#';
  for (let i = 0; i < 6; i++) {
    color += letters[Math.floor(Math.random() * 16)];
  }
  return color;
};

// Função para converter hex para RGBA com opacidade
const hexToRgba = (hex, opacity = 0.7) => {
  const [r, g, b] = hex.match(/\w\w/g).map((x) => parseInt(x, 16));
  return `rgba(${r}, ${g}, ${b}, ${opacity})`;
};

const App = () => {
  const [people, setPeople] = useState([]);
  const [formData, setFormData] = useState({ name: '', email: '', birthdate: '', id: null });
  const [errors, setErrors] = useState({});
  const [successMessage, setSuccessMessage] = useState('');
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [personToDelete, setPersonToDelete] = useState(null);
  const [viewState, setViewState] = useState('form'); // 'form' or 'list'

  // Validação do formulário
  const validateForm = () => {
    const { name, email, birthdate } = formData;
    const today = new Date();
    const inputDate = new Date(birthdate);
    let formErrors = {};

    if (!name) formErrors.name = 'Nome é obrigatório!';
    if (!email || !/\S+@\S+\.\S+/.test(email)) formErrors.email = 'E-mail inválido!';
    if (!birthdate) formErrors.birthdate = 'Data de nascimento é obrigatória!';
    if (inputDate > today) formErrors.birthdate = 'Data de nascimento não pode ser no futuro!';
    if (inputDate.getFullYear() < 1900 || inputDate.getFullYear() > today.getFullYear()) formErrors.birthdate = 'Ano inválido!';

    return formErrors;
  };

  // Envio do formulário
  const handleSubmit = (e) => {
    e.preventDefault();
    const validationErrors = validateForm();

    if (Object.keys(validationErrors).length === 0) {
      const randomColor = generateRandomColor();
      const colorWithOpacity = hexToRgba(randomColor, 0.20);

      if (formData.id) {
        setPeople(people.map(person => person.id === formData.id ? formData : person));
      } else {
        setPeople([...people, { ...formData, id: Date.now(), color: colorWithOpacity }]);
        setSuccessMessage('Pessoa cadastrada com sucesso!');
        setTimeout(() => setSuccessMessage(''), 3000); // Limpa a mensagem após 3 segundos
      }

      setFormData({ name: '', email: '', birthdate: '', id: null });
      setErrors({});
      setViewState('list');
    } else {
      setErrors(validationErrors);
    }
  };

  // Mudanças no formulário
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  // Exibição de lista ou formulário
  const toggleView = (view) => {
    setViewState(view);
    setErrors({});
  };

  // Editar pessoa
  const handleEdit = (id) => {
    const personToEdit = people.find(person => person.id === id);
    setFormData({ ...personToEdit });
    setViewState('form');
  };

  // Modal de confirmação de exclusão
  const handleDelete = (id) => {
    setPersonToDelete(id);
    setIsModalOpen(true);
  };

  const confirmDelete = () => {
    setPeople(people.filter(person => person.id !== personToDelete));
    setIsModalOpen(false);
    setPersonToDelete(null);
  };

  const cancelDelete = () => {
    setIsModalOpen(false);
    setPersonToDelete(null);
  };

  return (
    <div className="App">
      <div className="header-container">
        <h1 className="animated-title">Desafio Front-end</h1>
        <p className="animated-subtitle">Cadastro e listagem de pessoas</p>
      </div>

      <div className="buttons-container">
        <button onClick={() => toggleView('form')} className="create-btn">Novo Cadastro</button>
        <button onClick={() => toggleView('list')} className="list-btn">Ver Lista</button>
      </div>

      {successMessage && <div className="success-message">{successMessage}</div>}

      {viewState === 'form' && (
        <div className="form-container">
          <h2>{formData.id ? 'Editar Pessoa' : 'Cadastrar Pessoa'}</h2>
          <form onSubmit={handleSubmit}>
            <div className="input-container">
              <label>Nome:</label>
              <input type="text" name="name" value={formData.name} onChange={handleChange} />
              {errors.name && <div className="error-balloon">{errors.name}</div>}
            </div>
            <div className="input-container">
              <label>E-mail:</label>
              <input type="email" name="email" value={formData.email} onChange={handleChange} />
              {errors.email && <div className="error-balloon">{errors.email}</div>}
            </div>
            <div className="input-container">
              <label>Data de Nascimento:</label>
              <input type="date" name="birthdate" value={formData.birthdate} onChange={handleChange} />
              {errors.birthdate && <div className="error-balloon">{errors.birthdate}</div>}
            </div>
            <button type="submit" className="create-btn">
              {formData.id ? 'Atualizar' : 'Cadastrar'}
            </button>
          </form>
        </div>
      )}

      {viewState === 'list' && (
        <div className="list-container">
          <h2>Lista de Pessoas</h2>
          {people.length === 0 ? <p>Não há pessoas cadastradas.</p> : (
            <div className="cards-wrapper">
              {people.map(person => (
                <div key={person.id} className="card" style={{ backgroundColor: person.color }}>
                  <div className="card-items-conteiner">
                    <div className="card-item"><FaIcons.FaUser size={18} color="#333" className="card-icon" /><strong>{person.name}</strong></div>
                    <div className="card-item"><FaIcons.FaEnvelope size={18} color="#333" className="card-icon" />{person.email}</div>
                    <div className="card-item"><FaIcons.FaBirthdayCake size={18} color="#333" className="card-icon" />{new Date(person.birthdate).toLocaleDateString('pt-BR')}</div>
                  </div>
                  <div className="card-btn-space">
                    <button className="save-btn" onClick={() => handleEdit(person.id)}>Editar</button>
                    <button className="cancel-btn" onClick={() => handleDelete(person.id)}>Excluir</button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      )}

      {/* Modal de confirmação de exclusão */}
      <Modal isOpen={isModalOpen} onClose={cancelDelete} onConfirm={confirmDelete} />
    </div>
  );
};

export default App;
