import React, { useState } from 'react';
import { addPerson, updatePerson, deletePerson, getAllPeople } from './services/PersonService';
import PersonForm from './components/PersonForm';
import PersonList from './components/PersonList';

function App() {
  const [people, setPeople] = useState(getAllPeople());
  const [personToEdit, setPersonToEdit] = useState(null);

  const handleSave = (updatedPerson) => {
    if (updatedPerson.id) {
      updatePerson(updatedPerson);
      setPeople([...people]); // Atualiza a lista de pessoas
      setPersonToEdit(null); // Limpa a pessoa em edição
    } else {
      addPerson(updatedPerson);
      setPeople([...people]); // Adiciona nova pessoa à lista
    }
  };

  const handleEdit = (person) => {
    setPersonToEdit(person);
  };

  return (
    <div className="App">
      <PersonForm onSave={handleSave} personToEdit={personToEdit} />
      <PersonList people={people} onEdit={handleEdit} />
    </div>
  );
}

export default App;
