import React, { useState } from 'react';
import PersonForm from './components/PersonForm';
import PersonList from './components/PersonList';
import { addPerson, deletePerson, updatePerson } from './services/PersonService';

const App = () => {
  const [people, setPeople] = useState([]);

  const handleAddPerson = (person) => {
    // Supondo que o addPerson retorne a pessoa com um id
    const newPerson = addPerson(person);
    setPeople([...people, newPerson]);
  };

  const handleEditPerson = (id) => {
    const personToEdit = people.find(person => person.id === id);
    // Lógica para editar
    if (personToEdit) {
      updatePerson(personToEdit);
      setPeople(people.map(person => 
        person.id === id ? { ...person, ...personToEdit } : person
      ));
    }
  };

  const handleDeletePerson = (id) => {
    if (window.confirm('Você tem certeza que deseja excluir esta pessoa?')) {
      deletePerson(id);
      setPeople(people.filter(person => person.id !== id));
    }
  };

  return (
    <div>
      <h1>Cadastro de Pessoas</h1>
      <PersonForm onAdd={handleAddPerson} />
      <PersonList 
        people={people} 
        onEdit={handleEditPerson} 
        onDelete={handleDeletePerson} 
      />
    </div>
  );
};

export default App;
