import React, { useState } from "react";

function PersonCard({ onSave, showEditForm, setShowEditForm }) {
  const [person, setPerson] = useState({
    name: "",
    email: "",
    date: "",
  });

  // Função de input
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setPerson({ ...person, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSave(person); // Chama a função para salvar a pessoa
    setPerson({ name: "", email: "", date: "" }); // Limpa os campos após o envio
  };

  return (
    <div>
      {/* Somente exibe o formulário quando showEditForm for true */}
      {showEditForm && (
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            name="name"
            placeholder="Nome"
            value={person.name}
            onChange={handleInputChange}
            required
          />
          <input
            type="email"
            name="email"
            placeholder="E-mail"
            value={person.email}
            onChange={handleInputChange}
            required
          />
          <input
            type="date"
            name="date"
            placeholder="Data de Nascimento"
            value={person.date}
            onChange={handleInputChange}
            required
          />
          <button type="submit">Criar Pessoa</button>
        </form>
      )}
    </div>
  );
}

export default PersonCard;
