import React, { useState, useEffect } from "react";

function PersonCard({ personToEdit, onSave, showEditForm, setShowEditForm }) {
  const [person, setPerson] = useState({
    name: "",
    email: "",
    date: "",
  });

  useEffect(() => {
    if (personToEdit) {
      setPerson(personToEdit); // Preenche os campos quando está editando uma pessoa
    }
  }, [personToEdit]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setPerson({ ...person, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSave(person);
  };

  return (
    <div>
      <button onClick={() => setShowEditForm(!showEditForm)}>
        {showEditForm
          ? "Cancelar"
          : personToEdit
          ? "Editar Pessoa"
          : "Adicionar Pessoa"}
      </button>
      {showEditForm && (
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            name="name"
            placeholder="Nome"
            value={person.name}
            onChange={handleInputChange}
            required
          />
          <input
            type="email"
            name="email"
            placeholder="E-mail"
            value={person.email}
            onChange={handleInputChange}
            required
          />
          <input
            type="date"
            name="date"
            placeholder="Data de Nascimento"
            value={person.date}
            onChange={handleInputChange}
            required
          />
          <button type="submit">
            {personToEdit ? "Salvar Alterações" : "Criar Pessoa"}
          </button>
        </form>
      )}
    </div>
  );
}

export default PersonCard;
