import React, { useState, useEffect } from 'react';

const PersonForm = ({ person, onSave }) => {
  const [name, setName] = useState(person ? person.name : '');
  const [email, setEmail] = useState(person ? person.email : '');
  const [dob, setDob] = useState(person ? person.dob : '');

  useEffect(() => {
    if (person) {
      setName(person.name);
      setEmail(person.email);
      setDob(person.dob);
    }
  }, [person]);

  const handleSubmit = (e) => {
    e.preventDefault();
    onSave({ id: person ? person.id : Date.now(), name, email, dob });
  };

  return (
    <form onSubmit={handleSubmit}>
      <input 
        type="text" 
        value={name} 
        onChange={(e) => setName(e.target.value)} 
        placeholder="Nome" 
        required 
      />
      <input 
        type="email" 
        value={email} 
        onChange={(e) => setEmail(e.target.value)} 
        placeholder="Email" 
        required 
      />
      <input 
        type="date" 
        value={dob} 
        onChange={(e) => setDob(e.target.value)} 
        required 
      />
      <button type="submit">{person ? 'Atualizar' : 'Cadastrar'}</button>
    </form>
  );
};

export default PersonForm;
