import React, { useState } from 'react';
import './App.css';
import Modal from './components/Modal'; // Importe o Modal

const App = () => {
  const [people, setPeople] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [showList, setShowList] = useState(false);
  const [formData, setFormData] = useState({ name: '', email: '', birthdate: '', id: null });
  const [errors, setErrors] = useState({});
  const [successMessage, setSuccessMessage] = useState('');
  const [isModalOpen, setIsModalOpen] = useState(false);  // Controla o estado do modal
  const [personToDelete, setPersonToDelete] = useState(null); // Pessoa a ser deletada

  const handleDelete = (id) => {
    setPersonToDelete(id);
    setIsModalOpen(true);  // Abre o modal de confirmação
  };

  const confirmDelete = () => {
    if (personToDelete) {
      setPeople(people.filter((person) => person.id !== personToDelete));
      setIsModalOpen(false);
      setPersonToDelete(null); // Limpa a pessoa a ser deletada
    }
  };

  const cancelDelete = () => {
    setIsModalOpen(false);
    setPersonToDelete(null); // Limpa a pessoa a ser deletada
  };

  return (
    <div className="App">
      <div className="header-container">
        <h1 className="animated-title">Desafio Front-end</h1>
        <p className="animated-subtitle">Cadastro e listagem de pessoas</p>
      </div>

      <div className="buttons-container">
        <button onClick={handleCreate} className="create-btn">
          Novo Cadastro
        </button>
        <button onClick={handleList} className="list-btn">
          Ver Lista
        </button>
      </div>

      {successMessage && <div className="success-message">{successMessage}</div>}

      {showForm && (
        <div className="form-container">
          <h2>{formData.id ? 'Editar Pessoa' : 'Cadastrar Pessoa'}</h2>
          <form onSubmit={handleSubmit}>
            {/* Formulário de cadastro */}
          </form>
        </div>
      )}

      {showList && (
        <div className="list-container">
          <h2>Lista de Pessoas</h2>
          {people.length === 0 ? (
            <p>Não há pessoas cadastradas.</p>
          ) : (
            <div className="cards-wrapper">
              {people.map((person) => (
                <div
                  key={person.id}
                  className="card"
                  style={{ backgroundColor: person.color }}
                >
                  <p><strong>{person.name}</strong></p>
                  <p>{person.email}</p>
                  <p>{new Date(person.birthdate + 'T00:00:00').toLocaleDateString('pt-BR')}</p>
                  <div className="card-btn-space">
                    <button className="save-btn" onClick={() => handleEdit(person.id)}>Editar</button>
                    <button className="cancel-btn" onClick={() => handleDelete(person.id)}>Excluir</button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      )}

      {/* Modal de confirmação de exclusão */}
      <Modal
        isOpen={isModalOpen}
        onClose={cancelDelete}
        onConfirm={confirmDelete}
      />
    </div>
  );
};

export default App;
