import React, { useState } from 'react';
import './App.css';

const App = () => {
  const [people, setPeople] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({ name: '', email: '', birthdate: '', id: null });
  const [errors, setErrors] = useState({});
  const [successMessage, setSuccessMessage] = useState('');

  // Função de validação
  const validateForm = () => {
    let formErrors = {};
    const { name, email, birthdate } = formData;

    if (!name) formErrors.name = 'Nome é obrigatório!';
    if (!email || !/\S+@\S+\.\S+/.test(email)) formErrors.email = 'E-mail inválido!';
    if (!birthdate) formErrors.birthdate = 'Data de nascimento é obrigatória!';

    return formErrors;
  };

  // Função para lidar com o envio do formulário
  const handleSubmit = (e) => {
    e.preventDefault();

    const validationErrors = validateForm();

    if (Object.keys(validationErrors).length === 0) {
      if (formData.id) {
        // Caso tenha ID, estamos editando a pessoa
        updatePerson(formData);
        setShowForm(false);
      } else {
        // Caso não tenha ID, estamos cadastrando uma nova pessoa
        addPerson({ ...formData, id: Date.now() });
        setSuccessMessage('Pessoa cadastrada com sucesso!');
        setTimeout(() => setSuccessMessage(''), 3000); // Limpa a mensagem após 3 segundos
      }
      setFormData({ name: '', email: '', birthdate: '', id: null });
      setErrors({}); // Limpa os erros após o envio bem-sucedido
    } else {
      setErrors(validationErrors); // Exibe os erros
    }
  };

  // Função para adicionar pessoa
  const addPerson = (person) => {
    setPeople([...people, person]);
  };

  // Função para editar pessoa
  const updatePerson = (updatedPerson) => {
    setPeople(people.map((person) => (person.id === updatedPerson.id ? updatedPerson : person)));
  };

  // Função para excluir pessoa
  const deletePerson = (id) => {
    setPeople(people.filter((person) => person.id !== id));
  };

  return (
    <div className="App">
      <div className="header-container">
        <h1 className="animated-title">Desafio Front-end</h1>
        <p className="animated-subtitle">Cadastro e listagem de pessoas</p>
      </div>
      <div className="buttons-container">
        <button onClick={() => setShowForm(true)} className="create-btn">
          Novo Cadastro
        </button>
        <button onClick={() => setShowForm(false)} className="list-btn">
          Ver Lista
        </button>
      </div>

      {successMessage && <div className="success-message">{successMessage}</div>}

      {showForm && (
        <div className="form-container">
          <h2>{formData.id ? 'Editar Pessoa' : 'Cadastrar Pessoa'}</h2>
          <form onSubmit={handleSubmit}>
            <div className="input-container">
              <label>Nome:</label>
              <input
                type="text"
                name="name"
                value={formData.name}
                onChange={(e) => setFormData({ ...formData, name: e.target.value })}
              />
              {errors.name && <div className="error-balloon">{errors.name}</div>}
            </div>
            <div className="input-container">
              <label>E-mail:</label>
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={(e) => setFormData({ ...formData, email: e.target.value })}
              />
              {errors.email && <div className="error-balloon">{errors.email}</div>}
            </div>
            <div className="input-container">
              <label>Data de Nascimento:</label>
              <input
                type="date"
                name="birthdate"
                value={formData.birthdate}
                onChange={(e) => setFormData({ ...formData, birthdate: e.target.value })}
              />
              {errors.birthdate && <div className="error-balloon">{errors.birthdate}</div>}
            </div>
            <button type="submit" className="create-btn">
              {formData.id ? 'Atualizar' : 'Cadastrar'}
            </button>
          </form>
        </div>
      )}

      {!showForm && (
        <div className="list-container">
          <h2>Lista de Pessoas</h2>
          {people.length === 0 ? (
            <p>Não há pessoas cadastradas.</p>
          ) : (
            <div className="cards-wrapper">
              {people.map((person) => (
                <PersonCard
                  key={person.id}
                  person={person}
                  onEdit={() => setFormData(person)}
                  onDelete={() => deletePerson(person.id)}
                />
              ))}
            </div>
          )}
        </div>
      )}
    </div>
  );
};

const PersonCard = ({ person, onEdit, onDelete }) => {
  return (
    <div className="card">
      <h3>{person.name}</h3>
      <p>{person.email}</p>
      <p>{person.birthdate}</p>
      <button onClick={onEdit}>Editar</button>
      <button onClick={onDelete}>Excluir</button>
    </div>
  );
};

export default App;
