import React, { useState, useEffect } from 'react';

const PersonForm = ({ person, onSave }) => {
  const [formData, setFormData] = useState({
    id: '',
    name: '',
    email: '',
    birthdate: ''
  });

  useEffect(() => {
    if (person) {
      setFormData({ ...person });
    }
  }, [person]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevState) => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSave(formData); // Envia os dados para o App.js para salvar ou atualizar
    setFormData({ id: '', name: '', email: '', birthdate: '' }); // Limpa o formulário
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>{person ? 'Editar Pessoa' : 'Adicionar Pessoa'}</h2>
      <label>
        Nome:
        <input
          type="text"
          name="name"
          value={formData.name}
          onChange={handleChange}
          required
        />
      </label>
      <label>
        Email:
        <input
          type="email"
          name="email"
          value={formData.email}
          onChange={handleChange}
          required
        />
      </label>
      <label>
        Data de Nascimento:
        <input
          type="date"
          name="birthdate"
          value={formData.birthdate}
          onChange={handleChange}
          required
        />
      </label>
      <button type="submit">{person ? 'Atualizar' : 'Adicionar'}</button>
    </form>
  );
};

export default PersonForm;
