export const formatDateToBR = (date) => {
    if (!date) return '';
    const isoDate = new Date(date);
    return isoDate.toLocaleDateString('pt-BR');
  };
  
  export const formatDateToISO = (date) => {
    if (!date) return '';
    const [day, month, year] = date.split('/');
    return `${year}-${month}-${day}`;
  };
