import React, { useState, useEffect } from "react";

function PersonCard({ personToEdit, onSave, showEditForm, setShowEditForm }) {
  const [person, setPerson] = useState({ name: "", email: "", age: "", date: "" });

  useEffect(() => {
    if (personToEdit) {
      setPerson({
        name: personToEdit.name,
        email: personToEdit.email,
        age: personToEdit.age,
        date: personToEdit.date,
      });
    }
  }, [personToEdit]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setPerson({ ...person, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSave(person);
  };

  return (
    <div>
      <button onClick={() => setShowEditForm(!showEditForm)}>
        {showEditForm ? "Cancelar" : personToEdit ? "Editar Pessoa" : "Adicionar Pessoa"}
      </button>
      {showEditForm && (
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            name="name"
            placeholder="Nome"
            value={person.name}
            onChange={handleInputChange}
          />
          <input
            type="email"
            name="email"
            placeholder="E-mail"
            value={person.email}
            onChange={handleInputChange}
          />
          <input
            type="number"
            name="age"
            placeholder="Idade"
            value={person.age}
            onChange={handleInputChange}
          />
          <input
            type="date"
            name="date"
            placeholder="Data de Nascimento"
            value={person.date}
            onChange={handleInputChange}
          />
          <button type="submit">{personToEdit ? "Salvar" : "Adicionar"}</button>
        </form>
      )}
    </div>
  );
}

export default PersonCard;
