let people = [
    { id: 1, name: 'João Silva', email: 'joao@email.com', birthdate: '1990-01-01' },
    { id: 2, name: 'Maria Oliveira', email: 'maria@email.com', birthdate: '1985-06-12' }
  ];
  
  export const fetchPeople = () => {
    return people;
  };
  
  export const savePerson = (person) => {
    if (person.id) {
      // Atualizando a pessoa
      people = people.map(p => p.id === person.id ? person : p);
    } else {
      // Adicionando nova pessoa
      person.id = people.length + 1; // Gerando um ID único
      people.push(person);
    }
  };
  
  export const deletePerson = (id) => {
    people = people.filter(person => person.id !== id);
  };
