import React, { useState } from 'react';
import './App.css';

const App = () => {
  const [people, setPeople] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [showList, setShowList] = useState(false);
  const [formData, setFormData] = useState({ name: '', email: '', birthdate: '' });
  const [errors, setErrors] = useState({});
  const [successMessage, setSuccessMessage] = useState('');

  const validateForm = () => {
    let formErrors = {};
    const { name, email, birthdate } = formData;

    if (!name) formErrors.name = 'Nome é obrigatório!';
    if (!email || !/\S+@\S+\.\S+/.test(email)) formErrors.email = 'E-mail inválido!';
    if (!birthdate) formErrors.birthdate = 'Data de nascimento é obrigatória!';

    return formErrors;
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const validationErrors = validateForm();

    if (Object.keys(validationErrors).length === 0) {
      if (formData.id) {
        // Atualiza a pessoa existente
        const updatedPeople = people.map((person) =>
          person.id === formData.id ? { ...formData } : person
        );
        setPeople(updatedPeople);
        setSuccessMessage('Pessoa editada com sucesso!');

        // Exibe a mensagem de sucesso e volta para a lista após 3 segundos
        setTimeout(() => {
          setShowForm(false);
          setShowList(true);
          setSuccessMessage('');
        }, 3000);
      } else {
        // Cria uma nova pessoa
        setPeople([...people, { ...formData, id: Date.now() }]);
        setSuccessMessage('Pessoa cadastrada com sucesso!');

        setTimeout(() => {
          setShowForm(false);
          setShowList(true);
          setSuccessMessage('');
        }, 3000);
      }

      setFormData({ name: '', email: '', birthdate: '' });
      setErrors({});
    } else {
      setErrors(validationErrors);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({ ...prevData, [name]: value }));
  };

  const handleList = () => {
    setShowList(true);
    setShowForm(false);
  };

  const handleCreate = () => {
    setShowForm(true);
    setShowList(false);
  };

  const handleEdit = (id) => {
    const personToEdit = people.find((person) => person.id === id);
    setFormData({ ...personToEdit });
    setShowForm(true);
    setShowList(false);
  };

  const handleCancel = () => {
    setShowForm(false);
    setShowList(true);
  };

  const handleDelete = (id) => {
    const confirmDelete = window.confirm('Tem certeza que deseja excluir?');
    if (confirmDelete) {
      setPeople(people.filter((person) => person.id !== id));
    }
  };

  return (
    <div className="App">
      <div className="header-container">
        <h1>Desafio Cadastro de Pessoas</h1>
      </div>

      <div className="buttons-container">
        <button className="create-btn" onClick={handleCreate}>Cadastrar Pessoa</button>
        <button className="list-btn" onClick={handleList}>Listar Pessoas</button>
      </div>

      {showForm && (
        <div className="form-container">
          <h2>{formData.id ? 'Editar Pessoa' : 'Cadastrar Pessoa'}</h2>
          {successMessage && <div className="success-message">{successMessage}</div>}
          <form onSubmit={handleSubmit}>
            <div className="input-container">
              <label>Nome:</label>
              <input
                type="text"
                name="name"
                value={formData.name}
                onChange={handleChange}
              />
              {errors.name && <div className="error-balloon">{errors.name}</div>}
            </div>

            <div className="input-container">
              <label>E-mail:</label>
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
              />
              {errors.email && <div className="error-balloon">{errors.email}</div>}
            </div>

            <div className="input-container">
              <label>Data de Nascimento:</label>
              <input
                type="date"
                name="birthdate"
                value={formData.birthdate}
                onChange={handleChange}
              />
              {errors.birthdate && <div className="error-balloon">{errors.birthdate}</div>}
            </div>

            <button className="submit-btn" type="submit">Salvar</button>
          </form>
        </div>
      )}

      {showList && (
        <div className="list-container">
          <h2>Lista de Pessoas</h2>
          {people.length > 0 ? (
            <ul className="people-list">
              {people.map((person) => (
                <li key={person.id} className="person-card">
                  <p>{person.name}</p>
                  <p>{person.email}</p>
                  <p>{person.birthdate}</p>
                  <button onClick={() => handleEdit(person.id)}>Editar</button>
                  <button onClick={() => handleDelete(person.id)}>Excluir</button>
                </li>
              ))}
            </ul>
          ) : (
            <p>Nenhuma pessoa cadastrada.</p>
          )}
        </div>
      )}
    </div>
  );
};

export default App;
