import React, { useState, useEffect } from "react";
import { getAllPeople, addPerson, updatePerson, deletePerson } from "./services/PersonService";
import PersonList from "./components/PersonList";
import PersonCard from "./components/PersonCard";

function App() {
  const [people, setPeople] = useState([]);
  const [personToEdit, setPersonToEdit] = useState(null);
  const [showEditForm, setShowEditForm] = useState(false);

  // Carregar as pessoas ao montar o componente
  useEffect(() => {
    const peopleData = getAllPeople();
    setPeople(peopleData);
  }, []);

  // Função para adicionar ou editar uma pessoa
  const handleSavePerson = (person) => {
    if (personToEdit) {
      updatePerson(person);
    } else {
      addPerson(person);
    }
    setPeople(getAllPeople());
    setPersonToEdit(null);
    setShowEditForm(false);
  };

  // Função para excluir pessoa
  const handleDeletePerson = (id) => {
    deletePerson(id);
    setPeople(getAllPeople());
  };

  return (
    <div className="App">
      <h1>Cadastro de Pessoas</h1>
      <button onClick={() => setShowEditForm(true)}>Adicionar Pessoa</button>
      <PersonList
        people={people}
        onEdit={setPersonToEdit}
        onDelete={handleDeletePerson}
      />
      <PersonCard
        personToEdit={personToEdit}
        onSave={handleSavePerson}
        showEditForm={showEditForm}
        setShowEditForm={setShowEditForm}
      />
    </div>
  );
}

export default App;
