import React, { useState } from 'react';
import './App.css';

function App() {
  const [isFormVisible, setIsFormVisible] = useState(false);
  const [people, setPeople] = useState([]);
  const [formData, setFormData] = useState({ name: '', age: '' });
  const [formErrors, setFormErrors] = useState({ name: '', age: '' });
  const [formSuccess, setFormSuccess] = useState('');

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();

    // Validando os campos
    let errors = {};
    if (!formData.name) errors.name = 'Nome é obrigatório!';
    if (!formData.age || isNaN(formData.age)) errors.age = 'Idade é obrigatória e deve ser um número!';
    setFormErrors(errors);

    if (Object.keys(errors).length === 0) {
      setPeople([
        ...people,
        { id: Date.now(), name: formData.name, age: formData.age }
      ]);
      setFormSuccess('Pessoa cadastrada com sucesso!');
      setFormData({ name: '', age: '' });
    }
  };

  const handleCancel = () => {
    setFormData({ name: '', age: '' });
    setFormErrors({ name: '', age: '' });
  };

  return (
    <div className="app-container">
      <header className="header-container">
        <h1>Cadastrar e Listar Pessoas</h1>
      </header>

      <div className="button-container">
        <button className="btn-cadastrar" onClick={() => setIsFormVisible(true)}>Cadastrar Pessoa</button>
        <button className="btn-listar" onClick={() => setIsFormVisible(false)}>Listar Pessoas</button>
      </div>

      {isFormVisible ? (
        <div className="form-container">
          <h2>Cadastrar Pessoa</h2>
          <form onSubmit={handleFormSubmit}>
            <div className="input-container">
              <input
                type="text"
                name="name"
                value={formData.name}
                onChange={handleInputChange}
                placeholder="Nome"
                className={formErrors.name ? 'error' : ''}
              />
              {formErrors.name && <div className="error-balloon">{formErrors.name}</div>}
            </div>
            <div className="input-container">
              <input
                type="text"
                name="age"
                value={formData.age}
                onChange={handleInputChange}
                placeholder="Idade"
                className={formErrors.age ? 'error' : ''}
              />
              {formErrors.age && <div className="error-balloon">{formErrors.age}</div>}
            </div>
            <button type="submit" className="submit-btn">Salvar</button>
          </form>
          {formSuccess && <div className="success-message">{formSuccess}</div>}
          <button className="cancel-btn" onClick={handleCancel}>Cancelar</button>
        </div>
      ) : (
        <div className="list-container">
          <h2>Lista de Pessoas</h2>
          {people.map((person) => (
            <div key={person.id} className="card">
              <strong>{person.name}</strong>
              <p>Idade: {person.age}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default App;
