import React, { useState, useEffect } from 'react';
import './styles/App.css';
import PersonList from './components/PersonList';
import PersonForm from './components/PersonForm';
import { fetchPeople, deletePerson, savePerson } from './services/PersonService';

function App() {
  const [people, setPeople] = useState([]);
  const [currentPerson, setCurrentPerson] = useState(null);

  useEffect(() => {
    setPeople(fetchPeople()); // Carrega as pessoas inicialmente
  }, []);

  const handleEdit = (person) => {
    setCurrentPerson(person); // Preenche o formulário para edição
  };

  const handleDelete = (id) => {
    const updatedPeople = deletePerson(id);
    setPeople(updatedPeople); // Atualiza o estado da lista
  };

  const handleSave = (person) => {
    const updatedPeople = savePerson(person);
    setPeople(updatedPeople); // Atualiza a lista após salvar
    setCurrentPerson(null); // Reseta o formulário
  };

  return (
    <div className="App">
      <h1>Cadastro de Pessoas</h1>
      <PersonForm person={currentPerson} onSave={handleSave} />
      <PersonList people={people} onEdit={handleEdit} onDelete={handleDelete} />
    </div>
  );
}

export default App;
