import React, { useState } from 'react';
import './App.css';

const App = () => {
  const [people, setPeople] = useState([]);
  const [viewMode, setViewMode] = useState('card'); // 'card' ou 'list'
  const [formData, setFormData] = useState({ name: '', email: '', birthdate: '', id: null });

  // Fun√ß√£o para alternar o modo de visualiza√ß√£o
  const toggleViewMode = () => {
    setViewMode(viewMode === 'card' ? 'list' : 'card');
  };

  // Fun√ß√£o para editar a pessoa
  const handleEdit = (id) => {
    const personToEdit = people.find((person) => person.id === id);
    setFormData({ ...personToEdit });
  };

  // Fun√ß√£o para excluir a pessoa
  const handleDelete = (id) => {
    setPeople(people.filter((person) => person.id !== id));
  };

  return (
    <div className="App">
      <h1>Gerenciador de Pessoas</h1>
      <button className="toggle-btn" onClick={toggleViewMode}>
        {viewMode === 'card' ? 'Ver em Lista' : 'Ver em Cards'}
      </button>

      <div className={`cards-container ${viewMode}`}>
        {people.length === 0 ? (
          <p>Nenhuma pessoa cadastrada.</p>
        ) : (
          people.map((person) => (
            <div key={person.id} className={`person-card ${viewMode}`}>
              {viewMode === 'card' ? (
                <>
                  <p><strong>{person.name}</strong></p>
                  <p>{person.email}</p>
                  <p>{new Date(person.birthdate).toLocaleDateString('pt-BR')}</p>
                  <div className="actions">
                    <button onClick={() => handleEdit(person.id)} className="edit-btn">
                      ‚úèÔ∏è
                    </button>
                    <button onClick={() => handleDelete(person.id)} className="delete-btn">
                      üóëÔ∏è
                    </button>
                  </div>
                </>
              ) : (
                <div className="person-list-row">
                  <span>{person.name}</span>
                  <span>{person.email}</span>
                  <span>{new Date(person.birthdate).toLocaleDateString('pt-BR')}</span>
                  <div className="actions">
                    <button onClick={() => handleEdit(person.id)} className="edit-btn">
                      ‚úèÔ∏è
                    </button>
                    <button onClick={() => handleDelete(person.id)} className="delete-btn">
                      üóëÔ∏è
                    </button>
                  </div>
                </div>
              )}
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default App;
