import React, { useState, useEffect } from "react";

function PersonCard({ personToEdit, onSave, setPersonToEdit }) {
  const [person, setPerson] = useState({ name: "", email: "", date: "" });
  const [isEditing, setIsEditing] = useState(false);

  // Definir a pessoa a ser editada quando o componente receber a propriedade
  useEffect(() => {
    if (personToEdit) {
      setPerson(personToEdit); // Preenche os campos com os dados da pessoa a ser editada
    }
  }, [personToEdit]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setPerson({ ...person, [name]: value });
  };

  const handleSave = () => {
    onSave(person); // Salva a pessoa, seja criando ou atualizando
    setIsEditing(false); // Desativa o modo de edição
  };

  const handleCancel = () => {
    setIsEditing(false); // Cancela a edição e retorna ao modo de exibição normal
    setPerson(personToEdit); // Restaura os valores originais
  };

  return (
    <div className="card">
      <div className="card-header">
        <h3>{person.name}</h3>
      </div>
      <div className="card-body">
        {isEditing ? (
          <>
            <input
              type="text"
              name="name"
              value={person.name}
              onChange={handleInputChange}
            />
            <input
              type="email"
              name="email"
              value={person.email}
              onChange={handleInputChange}
            />
            <input
              type="date"
              name="date"
              value={person.date}
              onChange={handleInputChange}
            />
          </>
        ) : (
          <>
            <p>Email: {person.email}</p>
            <p>Data de Nascimento: {person.date}</p>
          </>
        )}
      </div>
      <div className="card-footer">
        {isEditing ? (
          <>
            <button onClick={handleSave} className="save-btn">Salvar</button>
            <button onClick={handleCancel} className="cancel-btn">Cancelar</button>
          </>
        ) : (
          <>
            <button onClick={() => setIsEditing(true)} className="edit-btn">Editar</button>
          </>
        )}
      </div>
    </div>
  );
}

export default PersonCard;
