import React, { useState, useEffect } from "react";
import { getAllPeople, addPerson, updatePerson, deletePerson } from "./services/PersonService";
import PersonList from "./components/PersonList";
import PersonCard from "./components/PersonCard";

function App() {
  const [people, setPeople] = useState([]);
  const [showForm, setShowForm] = useState(false); // Controla se mostra o formulário ou a lista
  const [personToEdit, setPersonToEdit] = useState(null); // Pessoa a ser editada
  const [successMessage, setSuccessMessage] = useState(""); // Mensagem de sucesso ao cadastrar

  // Carregar as pessoas ao montar o componente
  useEffect(() => {
    const peopleData = getAllPeople();
    setPeople(peopleData);
  }, []);

  // Função para adicionar ou editar uma pessoa
  const handleSavePerson = (person) => {
    if (personToEdit) {
      updatePerson(person); // Atualiza a pessoa se estiver editando
    } else {
      addPerson(person); // Adiciona uma nova pessoa
      setSuccessMessage("Pessoa cadastrada com sucesso!"); // Exibe a mensagem de sucesso
    }
    setPeople(getAllPeople()); // Recarrega a lista de pessoas
    setPersonToEdit(null); // Limpa a pessoa a ser editada
    setShowForm(false); // Fecha o formulário após salvar
  };

  // Função para excluir pessoa
  const handleDeletePerson = (id) => {
    if (window.confirm("Tem certeza que deseja excluir esta pessoa?")) {
      deletePerson(id);
      setPeople(getAllPeople()); // Recarrega a lista de pessoas após exclusão
    }
  };

  return (
    <div className="App">
      <h1>Cadastro de Pessoas</h1>

      <div className="button-container">
        <button onClick={() => setShowForm(true)} className="button create-btn">Cadastrar Pessoa</button>
        <button onClick={() => setShowForm(false)} className="button list-btn">Listar Pessoas</button>
      </div>

      {successMessage && <p className="success-message">{successMessage}</p>} {/* Exibe a mensagem de sucesso */}

      {/* Formulário para cadastrar ou editar pessoa */}
      {showForm && (
        <PersonCard
          personToEdit={personToEdit}
          onSave={handleSavePerson}
          setPersonToEdit={setPersonToEdit}
        />
      )}

      {/* Lista de pessoas */}
      {!showForm && (
        <PersonList
          people={people}
          onEdit={setPersonToEdit}
          onDelete={handleDeletePerson}
        />
      )}
    </div>
  );
}

export default App;
