import React from 'react';
import PersonCard from './PersonCard';

const PersonList = ({ people, onEdit, onDelete }) => {
  if (!people || people.length === 0) {
    return <p>Nenhuma pessoa cadastrada.</p>;
  }

  return (
    <div className="person-list">
      {people.map((person) => (
        <PersonCard 
          key={person.id}
          person={person}
          onEdit={onEdit}
          onDelete={onDelete}
        />
      ))}
    </div>
  );
};

const handleDelete = (id) => {
    const confirmation = window.confirm("Você tem certeza que deseja excluir?");
    if (confirmation) {
      deletePerson(id); // Se confirmado, deleta a pessoa
    }
  };

export default PersonList;
