import React, { useState } from 'react';

const PersonCard = ({ person, onEdit, onDelete }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [editName, setEditName] = useState(person.name);
  const [editEmail, setEditEmail] = useState(person.email);
  const [editBirthDate, setEditBirthDate] = useState(person.birthDate);

  const handleSave = () => {
    const updatedPerson = {
      id: person.id,
      name: editName,
      email: editEmail,
      birthDate: editBirthDate,
    };
    onEdit(updatedPerson);  // Atualiza a pessoa com os novos dados
    setIsEditing(false);  // Fecha o modo de edição
  };

/*   const handleDelete = () => {
    const confirmDelete = window.confirm('Tem certeza de que deseja excluir essa pessoa?');
    if (confirmDelete) {
      onDelete(person.id);  // Exclui a pessoa apenas se o usuário confirmar
    }
  }; */

  return (
    <div className="card">
      <div className="card-header">
        <h2>{isEditing ? 'Editar Pessoa' : person.name}</h2>
      </div>
      <div className="card-body">
        {isEditing ? (
          <>
            <input
              type="text"
              value={editName}
              onChange={(e) => setEditName(e.target.value)}
            />
            <input
              type="email"
              value={editEmail}
              onChange={(e) => setEditEmail(e.target.value)}
            />
            <input
              type="date"
              value={editBirthDate}
              onChange={(e) => setEditBirthDate(e.target.value)}
            />
          </>
        ) : (
          <>
            <p>Email: {person.email}</p>
            <p>Data de Nascimento: {person.birthDate}</p>
          </>
        )}
      </div>
      <div className="card-footer">
        <button onClick={isEditing ? handleSave : () => setIsEditing(true)}>
          {isEditing ? 'Salvar' : 'Editar'}
        </button>
        <button className="delete" onClick={handleDelete}>
          Excluir
        </button>
      </div>
    </div>
  );
};

export default PersonCard;
